import React from "react";

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      address: "",
      message: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    const name = target.name;
    const address = target.address;
    const message = target.message

    this.setState({
      name : name,
      address: address,
      message: message
    });
  }

  handleSubmit(event) {
    alert(
      "A name was submitted: " +
        this.state.name +
        "  your address: " +
        this.state.address
    );
    event.preventDefault();
  }

  render() {
    return (
      <div className="row">
        <form className="col s12" onSubmit={this.handleSubmit}>
          <div className="row">
            <div className="input-field col s12">

              <input id="icon_prefix" type="text" class="validate" name="name" value={this.state.name} onChange={this.handleChange}/>
              <label for="icon_prefix">First Name</label>

</div>
<div className="input-field col s12">
              <input id="icon_prefix" type="text" class="validate" name="address" value={this.state.address} onChange={this.handleChange}/>
              <label for="icon_prefix">Address</label>
              </div>
<div className="input-field col s12"> 
              <textarea id="textarea1" className="materialize-textarea" name="message" value={this.state.message} onChange={this.handleChange} />
              <label for="textarea1">Textarea</label>
              </div>
<div className="input-field col s12"> 
              <input type="submit" value="Submit" />
              </div>


          </div>
        </form>
      </div>

      // <form onSubmit={this.handleSubmit}>
      //   <label>
      //     Name:
      //     <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
      //   </label>
      //   <label>
      //     Address:
      //     <input type="text" name="address" value={this.state.address} onChange={this.handleChange} />
      //   </label>
      //   <input type="submit" value="Submit" />
      // </form>
    );
  }
}

export default NameForm;
